    {
"classes": [
{
"class": "ProgrammingLanguage",
"description": "Information about programming languages",
"vectorIndexType": "hnsw",
"vectorizer": "text2vec-transformers",
"properties": [
{
"name": "name",
"dataType": ["text"],
"description": "Name of the programming language"
},
{
"name": "description",
